import React, { useState } from 'react'

const Add = () => {
    const[product, setProduct] = useState({
        product_name:"",
        product_description:"",
        product_price: null,
        product_image:"",
    })

    const handleChange = (e) => {
        setProduct(prev=>({...prev, [e.target.name]: e.target.value }))
    }

    const handleClick = e => {
        e.preventDefault() // tira o refresh automátic
    }
    console.log(product)
  return (
    <div className='form'>
        <h1>Add novo produto</h1>
        <input type="text" placeholder='Nome do produto' onChange={handleChange} name='product_name'/>
        <input type="text" placeholder='Descrição do produto'onChange={handleChange} name='description'/>
        <input type="number" placeholder='Preço do produto' onChange={handleChange} name='product_price'/>
        <input type="text" placeholder='imagem do produto' onChange={handleChange} name='product_image'/>
        <button onClick={handleClick}>Add</button>
    </div>
  )
}

export default Add
